import React from "react";
import { Link } from "react-router-dom";
import SpidermanShowcase from "./components/SpidermanShowcase";
import { Flex, Text, Heading } from "@radix-ui/themes";
import Divider from "./components/Divider";
import ImagePopup from "../general/ImagePopup";
import CanvasComponent from "../general/CanvasComponent";
import Documentation from "./components/Documentation";
import { ThreeD as ThreeDInfo } from "../../Info";
import GifPopup from "./components/GifPopup";
import ImageRotation from "./components/ImageRotation";
import { Look } from "../svg/Button";

function DateLog({ date }) {
  return <Text size="2">Finished {date}.</Text>;
}

function FullSuit() {
  return (
    <>
      <Divider />
      <Text size="5">
        Advanced Suit 2.0 <Look />
      </Text>
      <ImageRotation path="images/3d/ps5_full_suit/rotation/" />
      <DateLog date="October 2, 2023" />
      <ImagePopup src="images/3d/ps5_full_suit/stance.png" isSubtle={true} />
      <Documentation
        text={ThreeDInfo.fullSuit}
        src="images/3d/ps5_full_suit/spiderman_low_poly_scope.png"
      />
      <ImagePopup src="images/3d/ps5_full_suit/dual_180.png" />
    </>
  );
}

function SymbioteSuit() {
  return (
    <>
      <Divider />
      <Text size="5">
        Symbiote Suit <Look />
      </Text>
      <ImageRotation path="images/3d/symbiote_suit/rotation/" />
      <DateLog date="December 5, 2023" />
      <Documentation text={ThreeDInfo.symbioteSuit} />
      <ImagePopup src="images/3d/symbiote_suit/dual_180.png" />
    </>
  );
}

function Venom() {
  return (
    <>
      <Divider />
      <Text size="5">
        Venom <Look />
      </Text>
      <ImageRotation path="images/3d/venom/rotation/" mainImageIndex={0} />
      <DateLog date="November 21, 2023" />
      <Documentation text={ThreeDInfo.venom} />
      <ImagePopup src="images/3d/venom/venom_low_poly_scope.png" />
      <ImagePopup src="images/3d/venom/dual_180.png" />
    </>
  );
}

function Webshooter() {
  return (
    <>
      <Divider />
      <Text size="5">
        Spider-Man Web Shooter <Look />
      </Text>
      <ImageRotation path="images/3d/webshooter/rotation/" />
      <DateLog date="October 7, 2023" />
      <Documentation
        text={ThreeDInfo.webshooterInit}
        src="images/3d/webshooter/rubber_mat.png"
      />
      <Documentation text={ThreeDInfo.webshooter} />
    </>
  );
}

function NormalMask() {
  return (
    <>
      <Divider />
      <Text size="5">Spider-Man Mask</Text>
      <ImagePopup src="images/3d/mask/mask.png" />
      <DateLog date="September 15, 2023" />
      <Documentation text={ThreeDInfo.mask} />
      <ImagePopup src="images/3d/mask/mask_mat.png" />
      <Documentation text={ThreeDInfo.maskShaderEditor} />
    </>
  );
}

function SpidermanHand() {
  return (
    <>
      <Divider />
      <Text size="5">Spider-Man Hand</Text>
      <CanvasComponent
        modelPath="spiderman_hand_showcase.glb"
        intensity={0}
        camera={{ fov: 22.5 }}
        style={{ maxWidth: "100%", height: "50vh" }}
