import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import About from "./components/home/Home";
import Blog from "./components/blog/Blog";
import BlogPost from "./components/blog/components/BlogPost";
import Nav from "./components/nav/Nav";
import { Theme, Flex } from "@radix-ui/themes";
import { motion } from "framer-motion";
import React from "react";

function App() {
  return (
    <Theme
      accentColor="gray"
      grayColor="gray"
      panelBackground="solid"
      scaling="100%"
      radius="full"
    >
      <Router>
        <Routes>
          <Route
            path="/*"
            element={
              <React.Fragment>
                <Flex gap="6" className="app-container" align="start">
                  <Nav />
                  <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ duration: 1 }}
                    style={{ width: "100%" }}
                  >
                    <Routes>
                      <Route path="/" element={<Blog />} />
                      {/* <Route path="/blog" element={<Blog />} /> */}
                      <Route path="/blog/:blogName" element={<BlogPost />} />
                      <Route path="/:blogName" element={<BlogPost />} />
                    </Routes>
                  </motion.div>
                </Flex>
              </React.Fragment>
            }
          />
        </Routes>
      </Router>
    </Theme>
  );
}

export default App;
