import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Blog from "./components/blog/Blog";
import BlogPost from "./components/blog/components/BlogPost";
import { Theme, Flex } from "@radix-ui/themes";
import { motion } from "framer-motion";
import React from "react";
import Nav from "./components/nav/Nav";
import { useLocation } from "react-router-dom";

function RouteWithNav({ children }) {
  const location = useLocation();
  return (
    <React.Fragment>
      <Nav currentPath={location.pathname} />
      {children}
    </React.Fragment>
  );
}

function App() {
  const location = useLocation();

  return (
    <Theme
      accentColor="gray"
      grayColor="gray"
      panelBackground="solid"
      scaling="100%"
      radius="full"
    >
      <Router>
        <Routes>
          <Route
            path="/*"
            element={
              <React.Fragment>
                <Flex gap="6" className="app-container" align="start">
                  <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ duration: 1 }}
                    style={{ width: "100%" }}
                  >
                    <Flex gap="4" direction="column">
                      <Nav currentPath={location.pathname} />
                      <Routes>
                        <Route
                          path="/blog"
                          element={
                            <RouteWithNav>
                              <Blog />
                            </RouteWithNav>
                          }
                        />
                        <Route
                          path="/blog/:blogName"
                          element={
                            <RouteWithNav>
                              <BlogPost />
                            </RouteWithNav>
                          }
                        />
                      </Routes>
                    </Flex>
                  </motion.div>
                </Flex>
              </React.Fragment>
            }
          />
        </Routes>
      </Router>
    </Theme>
  );
}

export default App;
